contracts/LoanAgreement.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title Enforces loan terms such as repayment logic, timestamps, status, and trust interactions
/// @author Ethan Wong, Tony Wang

contract LoanAgreement {

    // Storage //////////////////////////////////////////////////////////////

    // loan status
    enum LoanStatus { Active, Repaid, Defaulted }

    // loan vars
    address public lender;
    address public borrower;
    uint256 public principal;
    uint256 public interestPercent;
    uint256 public dueTimestamp; // legacy use, not relied upon with installments
    uint256 public lateFeePercent;
    address public tokenAddress;
    address public factory;

    // installment-specific vars
    uint256 public totalInstallments;
    uint256 public installmentInterval;
    uint256 public startTimestamp;

    // contract state vars
    uint256 public repaidAmount;
    bool public markedDefault;

    // events
    event Repaid(address indexed payer, uint256 amount, uint256 totalRepaid);
    event FullyRepaid(address indexed loan);
    event DefaultMarked(address indexed loan, address indexed lender);

    // Functions ///////////////////////////////////////////////////////////////////////

    /// @notice                 constructor for loan agreement
    /// @param _lender          lender address
    /// @param _borrower        borrower address
    /// @param _amount          principal loan
    /// @param _token           ERC-20 token address
    /// @param _duration        loan second duration
    /// @param _interestPercent interest rate
    /// @param _lateFeePercent  penalty interest for late payment
    /// @param _installments    number of repayments expected
    /// @param _factory         factory contract address
    constructor(
        address _lender,
        address _borrower,
        uint256 _amount,
        address _token,
        uint256 _duration,
        uint256 _interestPercent,
        uint256 _lateFeePercent,
        uint256 _installments,
        address _factory
    ) {
        lender = _lender;
        borrower = _borrower;
        principal = _amount;
        tokenAddress = _token;
        interestPercent = _interestPercent;
        lateFeePercent = _lateFeePercent;
        totalInstallments = _installments;
        installmentInterval = _duration / _installments;
        factory = _factory;
        startTimestamp = block.timestamp;
        dueTimestamp = startTimestamp + _duration; // optional legacy usage
    }

    /// @notice         called by borrower to repay loan
    /// @param amount   amount to be repaid
    function repay(uint256 amount) external {
        // ensure user authorised
        require(msg.sender == borrower, "Only borrower can repay");

        // Reject the transaction if overpaying
        uint256 totalOwed = getTotalOwed();
        require(repaidAmount + amount <= totalOwed, "Repayment exceeds total owed");

        // ensure token transfer success
        IERC20 token = IERC20(tokenAddress);
        require(token.transferFrom(msg.sender, lender, amount), "Token transfer failed");

        // update contract state and emit event
        repaidAmount += amount;
        emit Repaid(msg.sender, amount, repaidAmount);

        // check if loan fully paid
        if (getStatus() == LoanStatus.Repaid) {
            // emit event and update borrower trust score
            emit FullyRepaid(address(this));
            // penalize if any missed installments, reward if all on time
            if (countMissedInstallments() > 0) {
                _updateTrustScore(-1);
            } else {
                _updateTrustScore(1);
            }
        }
    }

    /// @notice mark a loan as defaulted (by lender) after final due date
    function markDefault() external {
        // ensure user is authorised and loan is valid and overdue
        require(msg.sender == lender, "Only lender can mark default");
        require(getStatus() == LoanStatus.Active, "Loan not active or already handled");
        require(block.timestamp > startTimestamp + (totalInstallments * installmentInterval), "Loan is not overdue yet");

        // update contract state and emit event
        markedDefault = true;
        emit DefaultMarked(address(this), lender);

        // update borrower trust score
        _updateTrustScore(-2);
    }

    /// @notice         get loan status
    /// @return status  current loan status
    function getStatus() public view returns (LoanStatus) {
        // defaulted check
        if (markedDefault) return LoanStatus.Defaulted;

        // repaid check
        if (repaidAmount >= getTotalOwed()) return LoanStatus.Repaid;

        // otherwise active
        return LoanStatus.Active;
    }

    /// @dev        internal helper to update trust score via the factory
    /// @param x    score change amount
    function _updateTrustScore(int256 x) internal {
        (bool success, ) = factory.call(
            abi.encodeWithSignature("updateTrustScore(address,int256)", borrower, x)
        );
        require(success, "Trust score update failed");
    }

    /// @notice calculates total amount owed including interest and late fees
    /// @return totalOwed total amount due
    function getTotalOwed() public view returns (uint256 totalOwed) {
        totalOwed = principal + (principal * interestPercent) / 100;

        uint256 missed = countMissedInstallments();
        totalOwed += (principal * lateFeePercent * missed) / (100 * totalInstallments);
    }

    /// @notice calculates how many installment deadlines have passed but remain unpaid
    /// @return missed count of missed installments
    function countMissedInstallments() public view returns (uint256 missed) {
        uint256 expectedPayments = (block.timestamp - startTimestamp) / installmentInterval;
        if (expectedPayments > totalInstallments) expectedPayments = totalInstallments;

        uint256 expectedAmount = (principal + (principal * interestPercent) / 100)
            * expectedPayments / totalInstallments;

        if (expectedAmount > repaidAmount) {
            missed = expectedPayments - (repaidAmount * totalInstallments)
                / (principal + (principal * interestPercent) / 100);
        }
    }

    function getDueTimestamp() public view returns (uint256) {
        return dueTimestamp;
    }

    function getRepaidAmount() public view returns (uint256) {
        return repaidAmount;
    }
}

contracts/LoanFactory.sol.content:
/// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "./LoanAgreement.sol";

/// @title Central manager to deploy loan contracts and keep track of them
/// @author Ethan Wong, Tony Wang

contract LoanFactory {

    // Storage ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    
    address[] public loans;                                                                     // deployed loan tracker
    mapping(address => bool) public isValidLoan;                                                // map loan to validity
    mapping(address => address[]) public loansByBorrower;                                       // map borrower to their loans
    mapping(address => address[]) public loansByLender;                                         // map lender to their loans
    mapping(address => int256) public trustScores;                                              // map user to their trust score
    event LoanCreated(address indexed borrower, address indexed lender, address loanAddress);   // event emitted whenever loan created

    // Loan Agreement Functions //////////////////////////////////////////////////////////////////////////////////////////////////////

    /// @notice                 deploys and records a new LoanAgreement contract
    /// @param _borrower        address of borrower
    /// @param _amount          token value of loan
    /// @param _token           address of erc-20 token
    /// @param _duration        second duration of loan
    /// @param _interestPercent interest percentage
    /// @return loanAddress     address of deployed contract
    function createLoan(        
        address _borrower,
        uint256 _amount,
        address _token,
        uint256 _duration,
        uint256 _interestPercent,
        uint256 _lateFeePercent,
        uint256 _installments
    ) external returns (address loanAddress) {
        // create new instance
        LoanAgreement loan = new LoanAgreement(
            msg.sender,
            _borrower,
            _amount,
            _token,
            _duration,
            _interestPercent,
            _lateFeePercent,
            _installments,
            address(this)
        );

        // validate loan and append to tracking vars
        loanAddress = address(loan);
        loans.push(loanAddress);
        loansByBorrower[_borrower].push(loanAddress);
        loansByLender[msg.sender].push(loanAddress);
        isValidLoan[loanAddress] = true;

        // emit event for frontend to listen for
        emit LoanCreated(_borrower, msg.sender, loanAddress);
    }

    function getLoans() external view returns (address[] memory) {
        return loans;
    }

    function getLoansCount() external view returns (uint256) {
        return loans.length;
    }

    function getBorrowerLoansCount(address user) external view returns (uint256) {
        return loansByBorrower[user].length;
    }

    function getLenderLoansCount(address user) external view returns (uint256) {
        return loansByLender[user].length;
    }

    function getBorrowerLoans(address user) external view returns (address[] memory) {
        return loansByBorrower[user];
    }

    function getLenderLoans(address user) external view returns (address[] memory) {
        return loansByLender[user];
    }

    /// @notice         helper function to filter loans by status
    /// @param loansArr array of loan addresses (borrower or lender)
    /// @param status   loan status to match
    /// @return         filtered list of loan contract addresses
    function getUserLoansByStatus(address[] memory loansArr, LoanAgreement.LoanStatus status) internal view returns (address[] memory) {
        // first pass: count matching loans to synthetically malloc space
        uint256 count = 0;
        for (uint256 i = 0; i < loansArr.length; i++) {
            if (LoanAgreement(loansArr[i]).getStatus() == status) {
                count++;
            }
        }

        // second pass: collect matching loans to populate synthetically mallocd space
        address[] memory res = new address[](count);
        uint256 j = 0;
        for (uint256 i = 0; i < loansArr.length; i++) {
            if (LoanAgreement(loansArr[i]).getStatus() == status) {
                res[j++] = loansArr[i];
            }
        }

        return res;
    }

    /// @notice         filter borrower loans by specific status
    /// @param borrower address of borrower
    /// @param status   loan status to filter for
    /// @return         filtered list of loan contract addresses
    function getBorrowerLoansByStatus(address borrower, LoanAgreement.LoanStatus status) external view returns (address[] memory) {
        return getUserLoansByStatus(loansByBorrower[borrower], status);
    }

    /// @notice         filter lender loans by specific status
    /// @param lender   address of lender
    /// @param status   loan status to filter for
    /// @return         filtered list of loan contract addresses
    function getLenderLoansByStatus(address lender, LoanAgreement.LoanStatus status) external view returns (address[] memory) {
        return getUserLoansByStatus(loansByLender[lender], status);
    }


    // Trust Score Functions /////////////////////////////////////////////////////////////////////

    /// @notice             update a borrower's trust score
    /// @dev                this function can only be called by registered loanAgreement contracts
    /// @param _borrower    address of borrow
    /// @param _val         value to be added to trust score (can be negative)
    function updateTrustScore(address _borrower, int256 _val) external {
        require(isValidLoan[msg.sender], "Only a registered loan can call this function!");
        trustScores[_borrower] += _val;
    }

    /// @notice Get a borrower's trust score
    function getTrustScore(address _user) external view returns (int256) {
        return trustScores[_user];
    }

}

